import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../utils/auth';

const BACKEND = process.env.REACT_APP_BACKEND;
const sources = ['toi', 'thehindu', 'ht', 'indianexpress'];

export default function NewsFeed() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [activeSource, setActiveSource] = useState(null);
  const { token } = useAuth();

  const fetchSource = async (src) => {
    setLoading(true);
    setActiveSource(src);
    try {
      const res = await axios.get(`${BACKEND}/api/news/${src}`);
      // Add placeholder images based on source
      const itemsWithImages = res.data.map(item => ({
        ...item,
        image: getPlaceholderImage(item.source)
      }));
      setItems(itemsWithImages);
    } catch (error) {
      console.error("Error fetching news:", error);
    }
    setLoading(false);
  };

  const getPlaceholderImage = (source) => {
    const images = {
      toi: 'https://images.unsplash.com/photo-1585829365295-ab7cd400c7e9?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      thehindu: 'https://images.unsplash.com/photo-1504711434969-e33886168f5c?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      ht: 'https://images.unsplash.com/photo-1495020689067-958852a7765e?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      indianexpress: 'https://images.unsplash.com/photo-1586339949916-3e9457bef6d3?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80'
    };
    return images[source] || 'https://images.unsplash.com/photo-1585829365295-ab7cd400c7e9?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80';
  };

  const saveBookmark = async (item) => {
    try {
      const summary = await getSummary(item);
      await axios.post(`${BACKEND}/api/bookmarks`, 
        { title: item.title, link: item.link, summary, source: item.source }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Article bookmarked successfully!');
    } catch (error) {
      alert('Failed to bookmark article');
      console.error(error);
    }
  };

  const getSummary = async (item) => {
    try {
      const res = await axios.post(`${BACKEND}/api/summarize`, 
        { url: item.link, title: item.title }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      return res.data.summary;
    } catch (e) { 
      console.error(e); 
      return null; 
    }
  };

  const openNews = (url) => {
    window.open(url, '_blank');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-gray-100 p-6">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl font-bold text-gray-800 mb-8 text-center bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-purple-600">
          Smart News Aggregator
        </h1>

        <div className="flex flex-wrap justify-center gap-4 mb-10">
          {sources.map(s => (
            <button
              key={s}
              onClick={() => fetchSource(s)}
              className={`px-6 py-3 rounded-xl font-bold transition-all transform hover:scale-105 shadow-lg ${
                activeSource === s 
                  ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-blue-500/50' 
                  : 'bg-white text-gray-700 hover:bg-gray-50 shadow-gray-300/50'
              }`}
            >
              {s.toUpperCase()}
            </button>
          ))}
        </div>

        {loading ? (
          <div className="flex justify-center py-20">
            <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-blue-500"></div>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {items.map((it, idx) => (
              <div 
                key={idx} 
                className="bg-white rounded-2xl overflow-hidden shadow-xl hover:shadow-2xl transition-all duration-300 transform hover:-translate-y-1 cursor-pointer"
                onClick={() => openNews(it.link)}
              >
                <div className="relative h-48 overflow-hidden">
                  <img 
                    src={it.image} 
                    alt={it.title}
                    className="w-full h-full object-cover transition-transform duration-500 hover:scale-110"
                  />
                  <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-4">
                    <span className="text-xs font-semibold text-white bg-blue-600 px-2 py-1 rounded-full">
                      {it.source.toUpperCase()}
                    </span>
                  </div>
                </div>

                <div className="p-6">
                  <h2 className="text-xl font-bold text-gray-800 mb-3 line-clamp-2">
                    {it.title}
                  </h2>

                  <div className="flex justify-between items-center mt-4">
                    <button 
                      onClick={(e) => {
                        e.stopPropagation();
                        saveBookmark(it);
                      }}
                      className="px-4 py-2 bg-blue-100 text-blue-600 rounded-lg hover:bg-blue-200 transition-colors flex items-center gap-2"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" />
                      </svg>
                      Save
                    </button>
                    <SummaryInline item={it} token={token} />
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

function SummaryInline({ item, token }) {
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchSummary = async (e) => {
    e.stopPropagation();
    setLoading(true);
    try {
      const res = await axios.post(
        `${process.env.REACT_APP_BACKEND}/api/summarize`, 
        { url: item.link, title: item.title }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSummary(res.data.summary);
    } catch (e) { 
      console.error(e);
      setSummary("Failed to load summary");
    }
    setLoading(false);
  };

  return (
    <div>
      {summary ? (
        <div 
          className="text-gray-600 text-sm p-3 bg-gray-50 rounded-lg cursor-default"
          onClick={(e) => e.stopPropagation()}
        >
          {summary}
        </div>
      ) : (
        <button 
          onClick={fetchSummary}
          disabled={loading}
          className="px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition-colors flex items-center gap-2"
        >
          {loading ? (
            <>
              <svg className="animate-spin h-5 w-5" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Summarizing...
            </>
          ) : (
            <>
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7v8a2 2 0 002 2h6M8 7V5a2 2 0 012-2h4.586a1 1 0 01.707.293l4.414 4.414a1 1 0 01.293.707V15a2 2 0 01-2 2h-2M8 7H6a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2v-2" />
              </svg>
              Summarize
            </>
          )}
        </button>
      )}
    </div>
  );
}
