import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../utils/auth';
export default function Bookmarks(){
  const [bookmarks, setBookmarks] = useState([]);
  const { token } = useAuth();
  useEffect(()=>{ fetch(); }, []);
  const fetch = async ()=>{ const res = await axios.get(`/api/bookmarks`, { headers:{ Authorization:`Bearer ${token}` } }); setBookmarks(res.data); };
  return (
    <div className="container">
      <h2>Bookmarks</h2>
      <ul style={{marginTop:12}}>
        {bookmarks.map(b=> (
          <li key={b._id} style={{padding:10, border:'1px solid #ddd', marginBottom:8}}>
            <a href={b.link} target="_blank" rel="noreferrer" style={{fontWeight:600}}>{b.title}</a>
            <div style={{fontSize:13, marginTop:8}}>{b.summary}</div>
          </li>
        ))}
      </ul>
    </div>
  );
}
