import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../utils/auth';

const BACKEND = process.env.REACT_APP_BACKEND;
const sources = ['toi', 'thehindu', 'ht', 'indianexpress'];

export default function NewsFeed() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [activeSource, setActiveSource] = useState(null);
  const { token } = useAuth();

  const fetchSource = async (src) => {
    setLoading(true);
    setActiveSource(src);
    try {
      const res = await axios.get(`${BACKEND}/api/news/${src}`);
      setItems(res.data);
    } catch (error) {
      console.error("Error fetching news:", error);
    }
    setLoading(false);
  };

  const getSummary = async (item) => {
    try {
      const res = await axios.post(`${BACKEND}/api/summarize`, 
        { url: item.link, title: item.title }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      return res.data.summary;
    } catch (e) { 
      console.error(e); 
      return null; 
    }
  };

  const saveBookmark = async (item) => {
    try {
      const summary = await getSummary(item);
      await axios.post(`${BACKEND}/api/bookmarks`, 
        { title: item.title, link: item.link, summary, source: item.source }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Article bookmarked successfully!');
    } catch (error) {
      alert('Failed to bookmark article');
      console.error(error);
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-6 text-center">Smart News Aggregator</h1>

      <div className="flex flex-wrap justify-center gap-3 mb-8">
        {sources.map(s => (
          <button
            key={s}
            onClick={() => fetchSource(s)}
            className={`px-4 py-2 rounded-full font-medium transition-colors ${
              activeSource === s 
                ? 'bg-blue-600 text-white' 
                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
            }`}
          >
            {s.toUpperCase()}
          </button>
        ))}
      </div>

      {loading ? (
        <div className="flex justify-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
        </div>
      ) : (
        <div className="space-y-4">
          {items.map((it, idx) => (
            <article key={idx} className="bg-white rounded-lg shadow-md overflow-hidden border border-gray-100 hover:shadow-lg transition-shadow">
              <div className="p-5">
                <h2 className="text-xl font-semibold text-gray-800 mb-2">
                  <a 
                    href={it.link} 
                    target="_blank" 
                    rel="noreferrer"
                    className="hover:text-blue-600 transition-colors"
                  >
                    {it.title}
                  </a>
                </h2>

                <div className="flex flex-wrap gap-3 mt-4">
                  <button 
                    onClick={() => saveBookmark(it)}
                    className="px-3 py-1 bg-blue-50 text-blue-600 rounded-md hover:bg-blue-100 transition-colors flex items-center gap-1"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" />
                    </svg>
                    Bookmark
                  </button>
                  <SummaryInline item={it} token={token} />
                </div>
              </div>
            </article>
          ))}
        </div>
      )}
    </div>
  );
}

function SummaryInline({ item, token }) {
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchSummary = async () => {
    setLoading(true);
    try {
      const res = await axios.post(
        `${process.env.REACT_APP_BACKEND}/api/summarize`, 
        { url: item.link, title: item.title }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSummary(res.data.summary);
    } catch (e) { 
      console.error(e);
      setSummary("Failed to load summary");
    }
    setLoading(false);
  };

  return (
    <div>
      {summary ? (
        <div className="text-gray-600 text-sm mt-3 p-3 bg-gray-50 rounded-md">
          {summary}
        </div>
      ) : (
        <button 
          onClick={fetchSummary}
          disabled={loading}
          className="px-3 py-1 bg-gray-50 text-gray-600 rounded-md hover:bg-gray-100 transition-colors flex items-center gap-1"
        >
          {loading ? (
            <>
              <svg className="animate-spin h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Summarizing...
            </>
          ) : (
            <>
              <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7v8a2 2 0 002 2h6M8 7V5a2 2 0 012-2h4.586a1 1 0 01.707.293l4.414 4.414a1 1 0 01.293.707V15a2 2 0 01-2 2h-2M8 7H6a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2v-2" />
              </svg>
              Summarize
            </>
          )}
        </button>
      )}
    </div>
  );
}
